
/**
 * returns an array of life path names based on the helper object sent to it
 * @param {object} [lifePathsObject] the ACS response object (expected: e.response.helpers[0].lifepaths)
 */
exports.getLifePathsArray = function (lifePathsObject){
    
	/** loose validation of helperObject */
    if(lifePathsObject === null){  return null;    }
    
    /** an array that holds the names of life paths found in helperObject */
    var lifePathsArray = [];
    
    /** loop through every property in the lifepath scope and store them in lifePathsArray */
    for (var x in lifePathsObject){
        lifePathsArray.push(x);
    }
    
    return lifePathsArray;
};




